const { test, expect } = require('@playwright/test');

const apiUrl = process.env.API_URL || 'https://api.pingera.ru';

test('Проверка доступности API', async ({ request }) => {
  const response = await test.step(`Отправка GET-запроса на ${apiUrl}`, async () => {
    return await request.get(apiUrl);
  });

  await test.step('Проверка статуса ответа', async () => {
    expect(response.status()).toBe(200);
    console.log(`API доступно. Статус ответа: ${response.status()}`);
  });
});
