const { test, expect } = require('@playwright/test');

test('проверка элементов на главной', async ({ page }) => {
  // Переходим на главную страницу
  await page.goto('https://pingera.ru');
  
  // Проверяем, что заголовок h1 виден
  await expect(page.locator('h1')).toBeVisible();

  // Проверяем, что ссылка с текстом 'Вход' видна и активна
  await expect(page.locator('a:has-text("Вход")')).toBeEnabled();
  
  // Проверяем наличие футера
  await expect(page.locator('div.t420')).toBeVisible();
  
  console.log('Все ключевые элементы на странице присутствуют');
});
