const { test, expect } = require('@playwright/test');

test('тест производительности', async ({ page }) => {
  // Замеряем время начала загрузки
  const startTime = Date.now();

  // Переходим на страницу и ждём полной загрузки
  await page.goto('https://pingera.ru');
  await page.waitForLoadState('domcontentloaded');

  // Замеряем время окончания загрузки
  const endTime = Date.now();
  const loadTime = endTime - startTime;

  console.log(`Страница загрузилась за ${loadTime} мс`);

  // Проверяем, что время загрузки не превышает 5 секунд (5000 мс)
  expect(loadTime).toBeLessThan(5000);

  console.log('Страница загрузилась с приемлемой скоростью');
});
