const { test, expect } = require('@playwright/test');

test('тест поиска', async ({ page }) => {
  // Переходим на страницу с поиском
  await page.goto('https://pingera.ru/blog');

  // Ищем поле поиска и вводим запрос
  await page.locator('input[placeholder="Поиск..."]').fill('мониторинг');
  await page.locator('input[placeholder="Поиск..."]').press('Enter');

  // Ждём загрузки результатов поиска
  await page.waitForLoadState('domcontentloaded');

  // Проверяем, что отобразилось не менее 1 результата
  const searchResults = page.locator('.search-results .result-item');
  await expect(searchResults).toHaveCount(expect.toBeGreaterThanOrEqual(1));

  console.log('Поиск работает корректно, найдены результаты');
});
