const { test, expect } = require('@playwright/test');

test('тест формы входа', async ({ page }) => {
  // Переходим на страницу входа
  await page.goto(process.env.LOGIN_URL);

  // Вводим логин и пароль из переменных окружения
  await page.locator('input[name="username"]').fill(process.env.LOGIN_USER);
  await page.locator('input[name="password"]').fill(process.env.LOGIN_PASSWORD);

  // Нажимаем кнопку входа
  await page.locator('button[type="submit"]').click();

  // Ждём перенаправления и проверяем, что на странице появился элемент после авторизации
  await page.waitForURL(process.env.DASHBOARD_URL);
  await expect(page.locator('h1')).toHaveText('Панель управления');

  console.log('Авторизация прошла успешно');
});
