const { test, expect } = require('@playwright/test');

test('простой скриншот', async ({ page }) => {
  // Переходим на указанную страницу
  await page.goto('https://pingera.ru');

  // Ждём полной загрузки страницы
  await page.waitForLoadState('load');

  // Делаем полностраничный скриншот
  await page.screenshot({ 
    path: 'website-screenshot.png', 
    fullPage: true 
  });

  // Проверяем, что страница загрузилась успешно
  await expect(page).toHaveTitle(/Pingera/);

  console.log('Скриншот успешно сделан.');
});
