const { test, expect } = require('@playwright/test');

test('тест загрузки динамического элемента', async ({ page }) => {
  // Переходим на страницу, где есть динамический контент
  await page.goto('https://pingera.ru/features');

  // Ждём, пока загрузится элемент с классом .features-list
  // Playwright автоматически ждёт, пока элемент станет видимым, что удобно для динамического контента
  await page.waitForSelector('.features-list');

  // После того как элемент появился, проверяем, что он содержит хотя бы один дочерний элемент
  const featureItems = page.locator('.features-list .feature-item');
  await expect(featureItems).toHaveCount(expect.toBeGreaterThanOrEqual(1));

  console.log('Динамический контент успешно загружен и отображается.');
});
