const { test, expect } = require('@playwright/test');

test('тест загрузки динамического элемента', async ({ page }) => {
  // Переходим на главную страницу
  await page.goto('https://pingera.ru');

  // Ждём, пока элемент с ID 'rec1160784986' станет видимым
  await expect(page.locator('#rec1160784986')).toBeVisible();

  // Проверяем, что внутри этого блока есть хотя бы один дочерний элемент
  const featureItems = page.locator('#rec1160784986 .t-card__col');
  const count = await featureItems.count();
  expect(count).toBeGreaterThanOrEqual(1);

  console.log('Динамический контент успешно загружен и отображается.');
});
